package com.ashehata.caty;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.GridLayoutManager;
import androidx.recyclerview.widget.ItemTouchHelper;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.os.Bundle;
import android.util.Log;
import android.widget.Toast;

import com.ashehata.caty.home.User;
import com.ashehata.caty.home.UserAdapter;
import com.google.android.material.snackbar.Snackbar;

import java.util.ArrayList;

//2. impl for the interface
public class MainActivity extends AppCompatActivity implements UserAdapter.OnUserClicked {

    private RecyclerView recyclerView;
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);
        recyclerView = findViewById(R.id.rv_users);
        //.......
        ArrayList<User> myUsers = new ArrayList<>();
        myUsers.add(new User("item1", "mo", "data:image/jpeg;base64,"));
        myUsers.add(new User("item2", "mo", "data:image/jpeg;base64,"));
        myUsers.add(new User("item3", "mo", "data:image/jpeg;base64,"));
        myUsers.add(new User("item4", "mo", "data:image/jpeg;base64,"));
        myUsers.add(new User("item5", "mo", "data:image/jpeg;base64,"));
        myUsers.add(new User("item6", "mo", "data:image/jpeg;base64,"));
        myUsers.add(new User("item7", "mo", "data:image/jpeg;base64,"));
        myUsers.add(new User("item8", "mo", "data:image/jpeg;base64,"));
        myUsers.add(new User("item9", "mo", "data:image/jpeg;base64,"));

        UserAdapter mAdapter = new UserAdapter(myUsers, this);
        //mAdapter.registerView(this);
        LinearLayoutManager layoutManager =
                new LinearLayoutManager(this, LinearLayoutManager.VERTICAL, false);

        GridLayoutManager gridLayout = new GridLayoutManager(this, 3);

        recyclerView.setLayoutManager(layoutManager);
        recyclerView.setAdapter(mAdapter);

        ItemTouchHelper.SimpleCallback simpleItemTouchCallback = new ItemTouchHelper.SimpleCallback(ItemTouchHelper.DOWN | ItemTouchHelper.UP, ItemTouchHelper.LEFT | ItemTouchHelper.RIGHT) {

            @Override
            public boolean onMove(RecyclerView recyclerView, RecyclerView.ViewHolder viewHolder, RecyclerView.ViewHolder target) {
//                Toast.makeText(MainActivity.this, "on Move", Toast.LENGTH_SHORT).show();
//                return false;

                mAdapter.swapItems(recyclerView,viewHolder,target);
                return true;
            }

            @Override
            public void onSwiped(RecyclerView.ViewHolder viewHolder, int swipeDir) {
                Toast.makeText(MainActivity.this, "Item deleted...", Toast.LENGTH_SHORT).show();
                //Remove swiped item from list and notify the RecyclerView
                int position = viewHolder.getAdapterPosition();
                mAdapter.removeUser(position);
            }
        };

        ItemTouchHelper itemTouchHelper = new ItemTouchHelper(simpleItemTouchCallback);
        itemTouchHelper.attachToRecyclerView(recyclerView);

    }

    @Override
    public void onUserSingleClicked(User user) {
        //....
        Toast.makeText(this, user.getTitle(), Toast.LENGTH_SHORT).show();
    }

    @Override
    public void onUserLongClicked(User user) {
        //...
        Snackbar.make(recyclerView, user.getTitle(), Snackbar.LENGTH_SHORT).show();
    }
}